var suggestions=document.getElementById('suggestions');var userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(e){if(e.keyCode===191){e.preventDefault();userinput.focus();}
if(e.keyCode===27){userinput.blur();suggestions.classList.add('d-none');}}
document.addEventListener('click',function(event){var isClickInsideElement=suggestions.contains(event.target);if(!isClickInsideElement){suggestions.classList.add('d-none');}});document.addEventListener('keydown',suggestionFocus);function suggestionFocus(e){const focusableSuggestions=suggestions.querySelectorAll('a');const focusable=[...focusableSuggestions];const index=focusable.indexOf(document.activeElement);let nextIndex=0;if(e.keyCode===38){e.preventDefault();nextIndex=index>0?index-1:0;focusableSuggestions[nextIndex].focus();}
else if(e.keyCode===40){e.preventDefault();nextIndex=index+1<focusable.length?index+1:index;focusableSuggestions[nextIndex].focus();}}
(function(){var index=new FlexSearch({preset:'score',cache:true,doc:{id:'id',field:['title','description','content',],store:['href','title','description',],},});var docs=[{id:0,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/introduction/",title:"Introduction",description:"Platform that allows organizations to manage revenue, expenses, governance, and other business functions",content:"\u003ch3 id=\"tokens\"\u003eTokens\u003c/h3\u003e\n\u003cp\u003eTokens represent a core component of managing your team. You configure your own token to be used for voting and financial rewards. It can be a single token, or you can create multiple tokens that are used for different purposes. To get started, we recommend a non-transferable token for voting and a transferable token for rewards.\u003c/p\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eTokens keep track of rewards and influence\u003c/div\u003e\n\u003c/div\u003e\n\u003ch3 id=\"roles-and-projects\"\u003eRoles and Projects\u003c/h3\u003e\n\u003cp\u003ePost roles and projects with clear descriptions so members can find work, and reward them with recurring salaries or one-time bounties.\u003c/p\u003e\n\u003ch2 id=\"badges\"\u003eBadges\u003c/h2\u003e\n\u003cp\u003eAward badges to members for performing a certain duty, holding an authority, completion of training, or for showing off their achievements.\u003c/p\u003e\n\u003cp\u003eBadges can optionally include extra kickers on top of members' salary or project pay.\u003c/p\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eRead more about credentials\u003c/div\u003e\n\u003c/div\u003e\n\u003ch2 id=\"identity-and-membership\"\u003eIdentity and Membership\u003c/h2\u003e\n\u003cp\u003eWe use account names instead of cryptic public keys. Members can establish an off-chain profile (GDPR compliant), and for those you may have never met, our social media proof guarantees that the person on your team is the same person they claim to be on LinkedIn, Twitter, Github, or other common social media sites.\u003c/p\u003e\n\u003ch2 id=\"proven-reporting\"\u003eProven Reporting\u003c/h2\u003e\n\u003cp\u003eBlockchain reports are typically esoteric, hard to read, and completely foreign to career accountants. They aren\u0026rsquo;t very good at monitoring business performance either. The platform supports time-tested double-entry accounting, with easy-to-use income statements, balance sheets, and budgets. Anyone who has used Quickbooks, NetSuite or SAP will feel right at home.\u003c/p\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eBecause accountants should love blockchain\u003c/div\u003e\n\u003c/div\u003e\n\u003ch2 id=\"web--mobile\"\u003eWeb \u0026amp; Mobile\u003c/h2\u003e\n\u003cp\u003eOur beautiful user interfaces eliminate the intimidating and confusing experience of typical blockchain applications and block explorers.\u003c/p\u003e\n\u003ch2 id=\"treasury\"\u003eTreasury\u003c/h2\u003e\n\u003cp\u003eAccept donations and investments from the community in a trustless, decentralized manner using bitcoin, Ethereum, or other common cryptocurrencies. Optionally listing your token on the exchange provides automated liquidity.\u003c/p\u003e\n\u003cp\u003eNative support for cross-chain multisignature allows you to establish an executive or financial committee responsible for handling your treasury.\u003c/p\u003e\n\u003ch2 id=\"open-source\"\u003eOpen Source\u003c/h2\u003e\n\u003cp\u003eOpen source\u003c/p\u003e\n"},{id:1,href:"https://dappdever.github.io/doks-gh-pages/docs/prologue/introduction/",title:"Introduction",description:"Doks is a Hugo theme helping you build modern documentation websites that are secure, fast, and SEO-ready â€” by default.",content:"\u003ch2 id=\"get-started\"\u003eGet started\u003c/h2\u003e\n\u003cp\u003eThere are two main ways to get started with Doks:\u003c/p\u003e\n\u003ch3 id=\"tutorial\"\u003eTutorial\u003c/h3\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eThe Tutorial is intended for novice to intermediate users.\u003c/div\u003e\n\u003c/div\u003e\n\u003cp\u003eStep-by-step instructions on how to start a new Doks project. \u003ca href=\"https://getdoks.org/tutorial/introduction/\"\u003eTutorial â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id=\"quick-start\"\u003eQuick Start\u003c/h3\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eThe Quick Start is intended for intermediate to advanced users.\u003c/div\u003e\n\u003c/div\u003e\n\u003cp\u003eOne page summary of how to start a new Doks project. \u003ca href=\"https://dappdever.github.io/doks-gh-pages/docs/prologue/quick-start/\"\u003eQuick Start â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id=\"go-further\"\u003eGo further\u003c/h2\u003e\n\u003cp\u003eRecipes, Reference Guides, Extensions, and Showcase.\u003c/p\u003e\n\u003ch3 id=\"recipes\"\u003eRecipes\u003c/h3\u003e\n\u003cp\u003eGet instructions on how to accomplish common tasks with Doks. \u003ca href=\"https://getdoks.org/docs/recipes/project-configuration/\"\u003eRecipes â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id=\"reference-guides\"\u003eReference Guides\u003c/h3\u003e\n\u003cp\u003eLearn how to customize Doks to fully make it your own. \u003ca href=\"https://getdoks.org/docs/reference-guides/security/\"\u003eReference Guides â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id=\"extensions\"\u003eExtensions\u003c/h3\u003e\n\u003cp\u003eGet instructions on how to add even more to Doks. \u003ca href=\"https://getdoks.org/docs/extensions/add-google-fonts/\"\u003eExtensions â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id=\"showcase\"\u003eShowcase\u003c/h3\u003e\n\u003cp\u003eSee what others have build with Doks. \u003ca href=\"https://getdoks.org/showcase/parietal-numerics-documentation/\"\u003eShowcase â†’\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id=\"contributing\"\u003eContributing\u003c/h2\u003e\n\u003cp\u003eFind out how to contribute to Doks. \u003ca href=\"https://getdoks.org/docs/contributing/how-to-contribute/\"\u003eContributing â†’\u003c/a\u003e\u003c/p\u003e\n"},{id:2,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/identity/",title:"Identity",description:"Know your members",content:"\u003cp\u003eModern organizations are borderless, virtual, and asynchronous. The platform features a suite of tools that allow members to know each other, if they want to.\u003c/p\u003e\n\u003ch3 id=\"account-names\"\u003eAccount names\u003c/h3\u003e\n\u003cp\u003eWe use account names, but not just public keys.\u003c/p\u003e\n\u003ch3 id=\"profiles\"\u003eProfiles\u003c/h3\u003e\n\u003cp\u003eLike all modern social platforms, we support profiles that allow members to describe themselves to the community. The platform uses blockchain authentication and encryption to secure appropriate information in a GDPR compliant manner.\u003c/p\u003e\n\u003ch3 id=\"double-blind-contact-gateway\"\u003eDouble Blind Contact Gateway\u003c/h3\u003e\n\u003cp\u003eIt is most common for members to share some contact information, such as their Element or Discord handle, an email, and perhaps even a telephone number.\u003c/p\u003e\n\u003cp\u003eHowever, the platform also supports notifications that are sent only \u003cem\u003eto\u003c/em\u003e a members' blockchain account and is forwarded to that members' preferred contact method. The recipient can then respond to the message and have it routed back to the original sender based on their preferred contact method. Neither party knows the other party\u0026rsquo;s contact information, or even preferred contact method.\u003c/p\u003e\n\u003cp\u003eThe gateway also supports end-to-end encrypted elements, although the counterparty account name and thread token are required to be readable by the gateway.\u003c/p\u003e\n\u003ch3 id=\"social-media-proofs\"\u003eSocial Media Proofs\u003c/h3\u003e\n\u003cp\u003eMembers can post a cryptographic proof that guarantees the person behind the blockchain account is the same person running a social media or Github account. This protects against Sybil attacks and also creates a sense of trust in your organization.\u003c/p\u003e\n\u003cp\u003eIdentity validators are also available in the community at large to provide additional confidence as required.\u003c/p\u003e\n"},{id:3,href:"https://dappdever.github.io/doks-gh-pages/docs/prologue/quick-start/",title:"Quick Start",description:"One page summary of how to start a new Doks project.",content:"\u003ch2 id=\"requirements\"\u003eRequirements\u003c/h2\u003e\n\u003cp\u003eDoks uses npm to install dependencies and run commands. Installing npm is pretty simple. Download and install \u003ca href=\"https://nodejs.org/\"\u003eNode.js\u003c/a\u003e (it includes npm) for your platform.\u003c/p\u003e\n\u003ch2 id=\"start-a-new-doks-project\"\u003eStart a new Doks project\u003c/h2\u003e\n\u003cp\u003eCreate a new site, change directories, install dependencies, and start development server.\u003c/p\u003e\n\u003ch3 id=\"create-a-new-site\"\u003eCreate a new site\u003c/h3\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"git clone https://github.com/h-enk/doks.git my-doks-site\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003egit clone https://github.com/h-enk/doks.git my-doks-site\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"change-directories\"\u003eChange directories\u003c/h3\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"cd my-doks-site\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003e\u003cspan class=\"nb\"\u003ecd\u003c/span\u003e my-doks-site\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"install-dependencies\"\u003eInstall dependencies\u003c/h3\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm install\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm install\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"start-development-server\"\u003eStart development server\u003c/h3\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run start\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run start\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eDoks will start the Hugo development webserver accessible by default at \u003ccode\u003ehttp://localhost:1313\u003c/code\u003e. Saved changes will live reload in the browser.\u003c/p\u003e\n\u003ch2 id=\"other-commands\"\u003eOther commands\u003c/h2\u003e\n\u003cp\u003eDoks comes with commands for common tasks. \u003ca href=\"https://dappdever.github.io/doks-gh-pages/docs/prologue/commands/\"\u003eCommands â†’\u003c/a\u003e\u003c/p\u003e\n"},{id:4,href:"https://dappdever.github.io/doks-gh-pages/docs/about/team/",title:"Team",description:"Our team loves to help organizations run free",content:"\u003cp\u003eNeed to figure out what/who goes here\u0026hellip;\u003c/p\u003e\n\u003cbr/\u003e\nKey leaders on Peru project, plus also connectivity to a legal entity for grant activity, cloud computer credits ...\n\u003cbr/\u003e\nPhotos, titles, contact info, etc....\n"},{id:5,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/membership/",title:"Membership",description:"Proposals allow team members to put their ideas to a vote",content:"\u003cp\u003eFlexible options allow for organizations to control membership in a number of different ways.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eSingle approver\u003c/li\u003e\n\u003cli\u003eSingle approver from a set of enrollers (see the Enroller badge)\u003c/li\u003e\n\u003cli\u003eMultisig enrollers\u003c/li\u003e\n\u003cli\u003eGovernance vote on enrollmennt\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:6,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/credentials/",title:"Credentials",description:"",content:"\u003cp\u003eCredentials, also known as badges, are artifacts or designations that can be assigned to a member.\u003c/p\u003e\n\u003cp\u003eSimilar to a role, badges are defined as an abstract document with an image, description, and optional payment coefficients. The badge is added to the \u0026lsquo;badge library\u0026rsquo; and becomes available to be assigned to individual members.\u003c/p\u003e\n\u003ch3 id=\"coefficients\"\u003eCoefficients\u003c/h3\u003e\n\u003cp\u003eThe payment coefficients are used to juice a badge holder\u0026rsquo;s pay on both salaries and one-time payments. Coefficients are compatible with any of your token types, governance, reward, or pegs.\u003c/p\u003e\n\u003cp\u003eCoeffecients may also be negative in order to handicap or penalize a member\u0026rsquo;s token balance.\u003c/p\u003e\n\u003ch3 id=\"powers\"\u003ePowers\u003c/h3\u003e\n\u003cp\u003eBadges can exist just for bragging rights or it may come with special powers.\u003c/p\u003e\n\u003ch3 id=\"badge-expiration\"\u003eBadge Expiration\u003c/h3\u003e\n\u003cp\u003eWhen badges are assigned, they may optionally come with a specific duration or time period of effectivity.\u003c/p\u003e\n\u003ch3 id=\"assignment\"\u003eAssignment\u003c/h3\u003e\n\u003cp\u003eBadges can be configured to be assignable by a single person, the approval of a governance vote, or based on certain pre-defined rules enforced by the blockchain.\u003c/p\u003e\n\u003ch3 id=\"default-library\"\u003eDefault Library\u003c/h3\u003e\n\u003cp\u003eOur default library contains the following badges:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eEnroller - admits applicants to the organization\u003c/li\u003e\n\u003cli\u003eTreasurer - multisignature authority on the cross-chain treasuries\u003c/li\u003e\n\u003cli\u003eFacilitator - this badge holder is trusted to run calls, ensuring fair participation and on-time wrap up\u003c/li\u003e\n\u003cli\u003eModerator\u003c/li\u003e\n\u003cli\u003eLamp Lighter\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:7,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/governance/",title:"Governance",description:"Proposals allow team members to put their ideas to a vote",content:"\u003cp\u003eThe platform supports a wide variety of voting methods and evaluation criteria.\u003c/p\u003e\n\u003ch2 id=\"voting-methods\"\u003eVoting Methods\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003eCumulative voting\u003c/li\u003e\n\u003cli\u003eRanked choice voting\u003c/li\u003e\n\u003cli\u003eConsensus voting\u003c/li\u003e\n\u003cli\u003eMajority rules\u003c/li\u003e\n\u003cli\u003eSuper majority\u003c/li\u003e\n\u003cli\u003eVariable quorum based on controversy\u003c/li\u003e\n\u003cli\u003eStake voting\u003c/li\u003e\n\u003cli\u003eElections\u003c/li\u003e\n\u003cli\u003eOne token, one vote\u003c/li\u003e\n\u003cli\u003eOne person, one vote\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"tokens\"\u003eTokens\u003c/h2\u003e\n\u003cp\u003eVoting power may be set to a single token or combination of tokens.\u003c/p\u003e\n\u003ch2 id=\"vote-power-decay\"\u003eVote Power Decay\u003c/h2\u003e\n\u003cp\u003eIf you select a non-transferable token to represent voting power, you may also optionally specify a decay algorithm. For example, voting power may be automatically reduced by 1% for each token holder, each week.\u003c/p\u003e\n\u003cp\u003eDecaying voting power serves to give more influence to members that are more active (earning vote tokens) recently.  It helps to keep ideas fresh and your organization moving forward.  Sometimes it makes sense, and sometimes is does not.\u003c/p\u003e\n\u003ch2 id=\"default-behavior\"\u003eDefault Behavior\u003c/h2\u003e\n\u003cp\u003eAlthough every organization differs, we have found this works well for many teams:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eNon-transferable voting token that is earned (VOICE token)\u003c/li\u003e\n\u003cli\u003eQuorum of 20% for to pass a proposal\u003c/li\u003e\n\u003cli\u003eProposal approval of 67% to pass\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:8,href:"https://dappdever.github.io/doks-gh-pages/docs/prologue/commands/",title:"Commands",description:"Doks comes with commands for common tasks.",content:"\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ’¡\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eYou can change the commands in the scripts section of `./package.json`.\u003c/div\u003e\n\u003c/div\u003e\n\u003ch2 id=\"start\"\u003estart\u003c/h2\u003e\n\u003cp\u003eStart local development server:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run start\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run start\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch2 id=\"lint\"\u003elint\u003c/h2\u003e\n\u003cp\u003eCheck scripts, styles, and markdown for errors:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run lint\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run lint\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"scripts\"\u003escripts\u003c/h3\u003e\n\u003cp\u003eCheck scripts for errors:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run lint:scripts\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run lint:scripts \u003cspan class=\"o\"\u003e[\u003c/span\u003e-- --fix\u003cspan class=\"o\"\u003e]\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"styles\"\u003estyles\u003c/h3\u003e\n\u003cp\u003eCheck styles for errors:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run lint:styles\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run lint:styles \u003cspan class=\"o\"\u003e[\u003c/span\u003e-- --fix\u003cspan class=\"o\"\u003e]\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"markdown\"\u003emarkdown\u003c/h3\u003e\n\u003cp\u003eCheck markdown for errors:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run lint:markdown\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run lint:markdown \u003cspan class=\"o\"\u003e[\u003c/span\u003e-- --fix\u003cspan class=\"o\"\u003e]\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch2 id=\"clean\"\u003eclean\u003c/h2\u003e\n\u003cp\u003eDelete temporary directories:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run clean\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run clean\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch2 id=\"build\"\u003ebuild\u003c/h2\u003e\n\u003cp\u003eBuild production website:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run build\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run build\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"functions\"\u003efunctions\u003c/h3\u003e\n\u003cp\u003eBuild Lambda functions:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run build:functions\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run build:functions\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch3 id=\"preview\"\u003epreview\u003c/h3\u003e\n\u003cp\u003eBuild production website including draft and future content:\u003c/p\u003e\n\u003cbutton class=\"btn-clipboard btn btn-sm btn-link\" data-clipboard-text=\"npm run build:preview\"\u003e\u003cspan class=\"copy-status\"\u003e\u003c/span\u003e\u003c/button\u003e\n\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run build:preview\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e"},{id:9,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/proposals/",title:"Proposals",description:"Proposals allow team members to put their ideas to a vote",content:"\u003cp\u003eProposals are documents that may have special powers if and when they are passed by the organization.\u003c/p\u003e\n\u003cp\u003eThe one common factor among all proposal types is that they may activate the organizational permission. This is activating full \u003cem\u003esudo\u003c/em\u003e permissions for the DAO.\u003c/p\u003e\n\u003ch2 id=\"policy-proposals\"\u003ePolicy proposals\u003c/h2\u003e\n\u003cp\u003ePolicy proposals are as it sounds. A passing policy proposal uses the organizational permission to attest, or certify, the content that is included within the proposal.\u003c/p\u003e\n\u003cp\u003eThese are most common for organizational constitutions or charters. Policy documents may include many sections or clauses, and they may be modified via a subsequent policy modification proposal. All of the content is stored on chain by default, but we also support uploading files to IPFS and anchoring them into a proposal.\u003c/p\u003e\n\u003ch2 id=\"role-proposals\"\u003eRole proposals\u003c/h2\u003e\n\u003cp\u003eA role proposal includes the following data:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eTitle\u003c/li\u003e\n\u003cli\u003eDescription\u003c/li\u003e\n\u003cli\u003eSalary amounts (per period, per token)\u003c/li\u003e\n\u003cli\u003eAvailable dates (begin and end)\u003c/li\u003e\n\u003cli\u003eMinimum commitment (e.g. no less than a 50% commitment)\u003c/li\u003e\n\u003cli\u003eAvailable slots (1 person budgeted? or many?)\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eWhen a role is approved, it becomes available for members to apply for.\u003c/p\u003e\n\u003ch2 id=\"assignment-proposals\"\u003eAssignment proposals\u003c/h2\u003e\n\u003cp\u003eMembers may apply for roles, and this proposal type supports the following information.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eApplication text\u003c/li\u003e\n\u003cli\u003eCommitment %\u003c/li\u003e\n\u003cli\u003ePayment composition (see Payments)\u003c/li\u003e\n\u003cli\u003eBegin and end dates\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eWhen an assignment proposal passes, the assignee is then able to claim compensation for the approved time period. The salary amounts from the role are automatically pro-rated according to approvals and they are adjusted to the assignee\u0026rsquo;s commitment level.\u003c/p\u003e\n\u003ch2 id=\"badge-proposals\"\u003eBadge proposals\u003c/h2\u003e\n\u003cp\u003eCreate a new badge or credential, or assign an existing badge to a member. See Credentials.\u003c/p\u003e\n\u003ch2 id=\"smart-contract-upgrades\"\u003eSmart contract upgrades\u003c/h2\u003e\n\u003cp\u003eProposals may be used to upgrade any smart contracts, although upgrades may also be assigned to a multisig authority of a technical steering committee.\u003c/p\u003e\n\u003ch2 id=\"organizational-authority\"\u003eOrganizational authority\u003c/h2\u003e\n\u003cp\u003eSince proposals activate an organizational authority, they can be used for nearly any transaction on chain that requires that authority.\u003c/p\u003e\n\u003cp\u003eThe platform supports also rollup proposals, where the final authority requires that a quorum and percentage of sub-organizations independently vote to approve.\u003c/p\u003e\n"},{id:10,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/roles/",title:"Roles",description:"Proposals allow team members to put their ideas to a vote",content:"\u003ch2 id=\"role-proposals\"\u003eRole proposals\u003c/h2\u003e\n\u003cp\u003eA role proposal includes the following data:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eTitle\u003c/li\u003e\n\u003cli\u003eDescription\u003c/li\u003e\n\u003cli\u003eSalary amounts (per period, per token)\u003c/li\u003e\n\u003cli\u003eAvailable dates (begin and end)\u003c/li\u003e\n\u003cli\u003eMinimum commitment (e.g. no less than a 50% commitment)\u003c/li\u003e\n\u003cli\u003eAvailable slots (1 person budgeted? or many?)\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eWhen a role is approved, it becomes available for members to apply for.\u003c/p\u003e\n"},{id:11,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/payments/",title:"Payments",description:"Proposals allow team members to put their ideas to a vote",content:"\u003cp\u003eThe platform supports endless tokenomic and payment opportunities. Here is what we have seen work the best.\u003c/p\u003e\n\u003ch4 id=\"reward-tokens\"\u003eReward tokens\u003c/h4\u003e\n\u003cp\u003eReward tokens are transferable, fungible tokens that are paid to donors, investors, and contributors as a reward in exchange for something of value provided to the organization.\u003c/p\u003e\n\u003cp\u003eFor a reward token to be valuable, it should have a purpose. For example, an open source software team may pay their reward token to their contributors.\u003c/p\u003e\n\u003cp\u003eThe reward token is also used (burned) when new users are granted access to a hosted instance of their software (freemium model).  This creates demand for their token. The users must purchase the token from the earners, which is they are rewarded for their contributions.\u003c/p\u003e\n\u003cp\u003eIt creates a full economic cycle that allows the community of users to fund continued innovation of the software they use.\u003c/p\u003e\n\u003ch4 id=\"escrow-payments\"\u003eEscrow payments\u003c/h4\u003e\n\u003cp\u003ePayments can be made as liquid tokens, meaning there are no restrictions are transferability (or selling). Payments may also be made as escrowed tokens.\u003c/p\u003e\n\u003cp\u003eEscrowed tokens are still owned by the holder and show in their balances, but they cannot be transfered until they are unlocked. Escrow payments are set to be unlocked based on one of the following criteria:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eUnlocked automatically on a specific date\u003c/li\u003e\n\u003cli\u003eUnlocked based on an event approval, as approved by voters\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eEvent-based escrow is commonly used for milestones. For example, a software team may escrow 25% of all payments as it is earned, and these tokens may be assigned to a series of milestones that must be voted on by the token holders.\u003c/p\u003e\n\u003cp\u003eEscrow payments help to keep organizations aligned around common goals.\u003c/p\u003e\n\u003ch4 id=\"non-transferable-governance-tokens\"\u003eNon-transferable governance tokens\u003c/h4\u003e\n\u003cp\u003eNon-transferable governance tokens may also be issued as payment. This token balance may be used to represent voting power on proposals.\u003c/p\u003e\n\u003ch3 id=\"example-configuration\"\u003eExample Configuration\u003c/h3\u003e\n\u003cp\u003eHere\u0026rsquo;s an example of a common configuration for assignment-based, salary payments. Although it seems complex, it creates an effective mix of incentives to attract and reward team members in an aligned manner.\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eMembers claims pay once per week\u003c/li\u003e\n\u003cli\u003e20% of Reward token salary paid in liquid form\u003c/li\u003e\n\u003cli\u003e30% of Reward token salary paid to event-based escrow (e.g. software \u0026ldquo;golive\u0026rdquo;)\u003c/li\u003e\n\u003cli\u003ePayment of non-transferable voting tokens\u003c/li\u003e\n\u003cli\u003e50% of salary paid in a USD-compatible, liquid peg token\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eThis level of flexibility is powerful because handling incentives in a meaningful way is paramount to driving successful teamwork. The example member earns voting power to influence future proposals, escrow tokens to keep her incentivized to see through to the end of the project, liquid reward tokens that can be transferred, and a USD-based stablecoin that can be exchanged immediately so that the member can support day-to-day needs such as groceries and rent.\u003c/p\u003e\n"},{id:12,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/accounting/",title:"Accounting",description:"Support for Income Statements \u0026 Balance Sheets",content:"\u003cp\u003eThe platform supports classic double-entry accounting in the same manner as many accounting packages used by businesses all over the world, including Quickbooks, NetSuite, ledger, GnuCash, and SAP.\u003c/p\u003e\n\u003cp\u003eOur implementation and support for double-entry accounting is a significant advantage when compared to other blockchain-powered organization platforms.\u003c/p\u003e\n\u003cdiv class=\"alert alert-warning d-flex\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003eðŸ‘‰\u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eWe believe support for traditional accounting methods on blockchain is critical for higher adoption rates\u003c/div\u003e\n\u003c/div\u003e\n\u003ch3 id=\"double-entry-ledgers\"\u003eDouble Entry Ledgers\u003c/h3\u003e\n\u003cp\u003eDouble entry booking has been the accounting best practice used for centuries. It is used by nearly all businesses, from a small one-person consultancy to public traded corporations.\u003c/p\u003e\n\u003cp\u003eIn many jurisdictions, tax compliance regulations require companies to use double-entry accounting. Among many other benefits, it enables the support of balance sheets and income statements.\u003c/p\u003e\n\u003ch3 id=\"balance-sheet\"\u003eBalance Sheet\u003c/h3\u003e\n\u003cp\u003eA balance sheet report shows an organization\u0026rsquo;s assets, liabilities, and equity. It includes ledger accounts such as \u003ccode\u003eaccounts payable\u003c/code\u003e (funds owed by the organization to another organization), \u003ccode\u003eaccounts receivable\u003c/code\u003e (funds owned to the organization), treasury amounts, and other assets such as intellectual property, inventory, and equipment.\u003c/p\u003e\n\u003cp\u003eBalance sheets are a point-in-time snapshot of the organization\u0026rsquo;s financial landscape.\u003c/p\u003e\n\u003cp\u003eHere\u0026rsquo;s an example view of a balance sheet\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/platform/accounting/balance-sheet_hude2e71f3aa7b8636755d7315498d593b_103393_20x0_resize_box_2.png\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/platform/accounting/balance-sheet.png\" width=\"610\" height=\"483\" alt=\"Balance Sheet\"\u003e\u003c/p\u003e\n\u003ch3 id=\"income-statement\"\u003eIncome Statement\u003c/h3\u003e\n\u003cp\u003eAn income statement report shows an organization\u0026rsquo;s financial performance over a period of time, such as a quarter or year.\u003c/p\u003e\n\u003cp\u003eIt shows the organization\u0026rsquo;s revenue compared to expenses. It includes ledger accounts such as \u003ccode\u003ecloud services\u003c/code\u003e, \u003ccode\u003esalaries\u003c/code\u003e, and \u003ccode\u003esales\u003c/code\u003e.\u003c/p\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/platform/accounting/income-statement_hua1ba38f10793d79b1614ceb33e9f448e_35406_20x0_resize_q75_box.jpg\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/platform/accounting/income-statement.jpg\" width=\"528\" height=\"400\" alt=\"Income Statement\"\u003e\n"},{id:13,href:"https://dappdever.github.io/doks-gh-pages/docs/document-graph/structure/",title:"Data Model",description:"Flexible graph of KV pairs",content:"\u003cp\u003eThe platform stores on- and off-chain data in a flexible, content-addressed document graph.\u003c/p\u003e\n\u003cp\u003eA graph is a set of documents that are connected with labeled, directional edges.\u003c/p\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/structure/doc-graph-structure_hu1e3880adcffcf0be29e4975856951acd_120037_20x0_resize_box_2.png\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/structure/doc-graph-structure.png\" width=\"715\" height=\"484\" alt=\"Document Graph\"\u003e\n\u003ch3 id=\"document\"\u003eDocument\u003c/h3\u003e\n\u003cp\u003eA document is comprised of three primary components:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003ea header\u003c/li\u003e\n\u003cli\u003econtent - an ordered \u0026lsquo;list of labeled lists\u0026rsquo; of key-value pairs\u003c/li\u003e\n\u003cli\u003ecertificates\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch5 id=\"header\"\u003eHeader\u003c/h5\u003e\n\u003cp\u003eThe document header stores the following:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003ecreator (blockchain account)\u003c/li\u003e\n\u003cli\u003ecreated_date\u003c/li\u003e\n\u003cli\u003ehash of the contents\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eThe document graph uses content-addressing, which means the unique identifier of the document is a hash of the contents.  See here for the benefits of content-addressing.\u003c/p\u003e\n\u003ch5 id=\"content\"\u003eContent\u003c/h5\u003e\n\u003cp\u003eThe content of a document is an ordered list of labeled lists of key-value pairs. Here\u0026rsquo;s a psuedo-code representation:\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003eContent:\n  Group: Label: 'content-group #1', Sequence: 1\n    Item: Key: 'content-item A', Value: 'content-item-value A', Sequence: 1\n    Item: Key: 'content-item B', Value: 'content-item-value B', Sequence: 2\n    Item: Key: 'content-item C', Value: 'content-item-value C', Sequence: 3\n    ...\n  Group: Label: 'content-group #2', Sequence: 2\n    Item: Key: 'content-item D', Value: 'content-item-value D', Sequence: 1\n    Item: Key: 'content-item E', Value: 'content-item-value E', Sequence: 2\n    Item: Key: 'content-item F', Value: 'content-item-value F', Sequence: 3\n    ...\n  ...\n\u003c/code\u003e\u003c/pre\u003e\u003cp\u003eThe \u003ccode\u003econtent-item-values\u003c/code\u003e can be of any native EOSIO data type. Any data that can be stored within a smart contract can be stored within a document.\u003c/p\u003e\n\u003ch5 id=\"certificates\"\u003eCertificates\u003c/h5\u003e\n\u003cp\u003eCertificates represent an attestation by any available authorization on-chain, which could be an organization member, group of members, any outside party, etc.\u003c/p\u003e\n\u003cp\u003eA certificates is comprised of:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003ecertified_date\u003c/li\u003e\n\u003cli\u003epermission used to certify (the account)\u003c/li\u003e\n\u003cli\u003enotes, any abitrary screen added by the certifier\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"edges\"\u003eEdges\u003c/h3\u003e\n\u003cp\u003eEdges are a labeled, directional link between two documents. Edges are also content-addressed across the ``from-document\u003ccode\u003e, \u003c/code\u003eto-document\u003ccode\u003e, and \u003c/code\u003eedge-name```.  Other than that, there are no limitations on cardinality and uniqueness.\u003c/p\u003e\n\u003cp\u003eEdges are not scored, or ranked, as in some graph theory, but this is something we may add in the future.\u003c/p\u003e\n\u003cp\u003eEdges are indexed based on every combination of ``from-document\u003ccode\u003e, \u003c/code\u003eto-document\u003ccode\u003e, and \u003c/code\u003eedge-name\u003ccode\u003e, and also by \u003c/code\u003ecreated_date\u003ccode\u003eand\u003c/code\u003ecreator```.\u003c/p\u003e\n\u003ch3 id=\"merkle-tree\"\u003eMerkle tree\u003c/h3\u003e\n\u003cp\u003eA document graph enjoys all of the benefits of being a merkle tree, including verification by its merkle root.\u003c/p\u003e\n\u003ch4 id=\"badge-and-membership-example\"\u003eBadge and membership example\u003c/h4\u003e\n\u003cp\u003eHere is an example of how the graph is used for organizational membership, badges, and payments.\u003c/p\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/structure/graph-example_hu5f60382422f1218a2f326e33bf48bbfa_283772_20x0_resize_box_2.png\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/structure/graph-example.png\" width=\"1014\" height=\"700\" alt=\"Document Graph\"\u003e\n"},{id:14,href:"https://dappdever.github.io/doks-gh-pages/docs/daoctl/installation/",title:"Quick Start",description:"",content:"\u003cp\u003e\u003ccode\u003edaoctl\u003c/code\u003e is an open source, command line application for organizations written in Go. It is also a secure wallet for managing private keys and submitted transactions.\u003c/p\u003e\n\u003ch4 id=\"build\"\u003eBuild\u003c/h4\u003e\n\u003cp\u003eBuilding \u003ccode\u003edaoctl\u003c/code\u003e requires Go v15 and above.\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003egit clone https://github.com/hypha-dao/daoctl/\ncd daoctl\ngo build\n./daoctl -h\n\u003c/code\u003e\u003c/pre\u003e\u003ch4 id=\"download-binary\"\u003eDownload binary\u003c/h4\u003e\n\u003cp\u003ehttps://github.com/hypha-dao/daoctl/releases\u003c/p\u003e\n\u003ch4 id=\"run-help\"\u003eRun help\u003c/h4\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl --help                                                                                                                                 \nDecentralized Autonomous Organization (DAO) control application for Hypha DAO query and actions.\nQuery and manage roles, assignments, periods, payouts, and proposals.\n\nExample use:\n\tdaoctl get assignments --include-proposals\n\tdaoctl get treasury\n\nHypha - Dapps for a New World - visit online @ hypha.earth\n\nUsage:\n  daoctl [command]\n\nAvailable Commands:\n  backup      creates a local backup of current DAO data\n  close       close a proposal\n  create      create an object (e.g. proposal) based on the JSON file\n  edit        propose an edit to an object\n  get         get objects from the DAO on-chain smart contract\n  help        Help about any command\n  propose     propose a role\n  query       Query action history on the DAO for specific users\n  serve       Start the Hypha prometheus metrics server\n  set         set data on the DAO on-chain smart contract\n  treasury    multi-chain token/value redemption module\n  vault       The vault is a secure key store (wallet). Your key is stored encrypted by the passphrase.\n  vote        vote pass or fail on a ballot\n\nFlags:\n  -a, --active               show active objects (default true)\n      --config string        config file (default is ./daoctl.yaml)\n      --csv                  Output data as CSV to console - not supported on all commands yet\n      --debug                Enables verbose debug messages\n      --expiration int       Set time before transaction expires, in seconds. Defaults to 30 seconds. (default 30)\n      --failed-proposals     include a table with failed proposals\n  -f, --file string          filename\n  -h, --help                 help for daoctl\n  -o, --include-archive      include a table with the archive objects\n      --include-proposals    include a table with proposals in the output\n      --output-file string   Output CSV data to file - not supported on all commands yet (default \u0026quot;output.csv\u0026quot;)\n      --vault-file string    Wallet file that contains encrypted key material (default \u0026quot;./eosc-vault.json\u0026quot;)\n\nUse \u0026quot;daoctl [command] --help\u0026quot; for more information about a command.\n\u003c/code\u003e\u003c/pre\u003e\u003ch4 id=\"sample-configurationyaml\"\u003eSample configuration.yaml\u003c/h4\u003e\n\u003cp\u003eHere\u0026rsquo;s a sample configuration file for the testnet. You can save as a file and use \u003ccode\u003e--config\u003c/code\u003e then the file name as a parameter.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-yaml\" data-lang=\"yaml\"\u003e\u003cspan class=\"nt\"\u003eEosioEndpoint\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003ehttps://test.telos.kitchen\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eAssetsAsFloat\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"kc\"\u003etrue\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eDAOContract\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003edao.hypha\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eTreasury\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eTokenContract\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003ehusd.hypha\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eSymbol\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003eHUSD\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eContract\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003ebank.hypha\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eEthUSDTContract\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"m\"\u003e0xdac17f958d2ee523a2206206994597c13d831ec7\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eEthUSDTAddress\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003e0xC20f453a4B4995CA032570f212988F4978B35dDd\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e  \u003c/span\u003e\u003cspan class=\"nt\"\u003eBtcAddress\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003e35hfgfaUouzYixTUDV6CFqMiTSZcuNtTf9\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eTelosDecideContract\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003etrailservice\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eDAOUser\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003etreasurermmm\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003cspan class=\"w\"\u003e\u003c/span\u003e\u003cspan class=\"nt\"\u003eHyperionEndpoint\u003c/span\u003e\u003cspan class=\"p\"\u003e:\u003c/span\u003e\u003cspan class=\"w\"\u003e \u003c/span\u003e\u003cspan class=\"l\"\u003ehttps://testnet.telosusa.io/v2\u003c/span\u003e\u003cspan class=\"w\"\u003e\n\u003c/span\u003e\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e"},{id:15,href:"https://dappdever.github.io/doks-gh-pages/docs/document-graph/graph-access/",title:"Querying the Graph",description:"The Graph is available from smart contracts and via GraphQL",content:"\u003ch3 id=\"query\"\u003eQuery\u003c/h3\u003e\n\u003ch4 id=\"smart-contracts\"\u003eSmart Contracts\u003c/h4\u003e\n\u003cp\u003eThe graph is highly indexed in order to support fast query access from within smart contracts. Any content within the graph can be accessed without iteration and \u003ccode\u003eO(1)\u003c/code\u003e performance.\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e\u0026lt;example on-chain graph query\u0026gt;\n\u003c/code\u003e\u003c/pre\u003e\u003ch4 id=\"graphql\"\u003eGraphQL\u003c/h4\u003e\n\u003cp\u003eThe graph is cached off-chain using DGraph and can be queried using GraphQL.\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003e\u0026lt;example graphql query\u0026gt;\n\u003c/code\u003e\u003c/pre\u003e\u003ch3 id=\"graph-navigation\"\u003eGraph navigation\u003c/h3\u003e\n\u003cp\u003eThere is a GUI and CLI that enables general purpose graph navigation of the on-chain graph.\u003c/p\u003e\n\u003cp\u003eGraph viewer allows the user to select a document and view the contents on the side panel.\u003c/p\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/graph-access/graph-viewer_hu47e06f812eec8720a4180f15f7ee799d_765870_20x0_resize_box_2.png\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/graph-access/graph-viewer.png\" width=\"1671\" height=\"905\" alt=\"Square\"\u003e\n\u003cp\u003e\u003ccode\u003edaoctl\u003c/code\u003e offers a CLI-based graph navigator.\u003c/p\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/graph-access/daoctl-graph-navigation_hude67273e8a4578bb03641be20d2c795a_176582_20x0_resize_box_2.png\" data-src=\"https://dappdever.github.io/doks-gh-pages/docs/document-graph/graph-access/daoctl-graph-navigation.png\" width=\"1026\" height=\"813\" alt=\"Square\"\u003e\n"},{id:16,href:"https://dappdever.github.io/doks-gh-pages/docs/daoctl/commands/",title:"Commands",description:"",content:"\u003cp\u003e\u003ccode\u003edaoctl\u003c/code\u003e remains under heavy development. Expect breaking changes.\u003c/p\u003e\n\u003ch4 id=\"commands\"\u003eCommands\u003c/h4\u003e\n\u003ch5 id=\"view-roles\"\u003eView Roles\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get roles\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"include-proposals\"\u003eInclude proposals\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get roles --include-proposals\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"view-assignments\"\u003eView Assignments\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get payouts\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"view-treasury\"\u003eView Treasury\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get treasury\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"view-treasury-redemption-requests\"\u003eView Treasury Redemption Requests\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl treasury get requests\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"view-treasury-payments-fulfilling-requests\"\u003eView Treasury Payments (fulfilling requests)\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl treasury get payments\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"view-details-of-a-ballot\"\u003eView Details of a Ballot\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get ballot d4\n\u003c/code\u003e\u003c/pre\u003e\u003ch5 id=\"serve-prometheus-metrics\"\u003eServe prometheus metrics\u003c/h5\u003e\n\u003cpre\u003e\u003ccode\u003e./daoctl get ballot d4\n\u003c/code\u003e\u003c/pre\u003e\u003ch4 id=\"treasury-commands\"\u003eTreasury Commands\u003c/h4\u003e\n\u003cp\u003eThe treasury requires multisig approvals from treasurers. See below for creating payment and attestation records.\u003c/p\u003e\n\u003cp\u003eSubmitting a new payment against a Redemption Request\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003e\u003cspan class=\"c1\"\u003e# in the below command, the redemption_id is 6\u003c/span\u003e\n./daoctl --vault-file hyphanewyork.json treasury newpayment \u003cspan class=\"m\"\u003e6\u003c/span\u003e \u003cspan class=\"s2\"\u003e\u0026#34;3500.00 HUSD\u0026#34;\u003c/span\u003e --network BTC --trxid b475e94c6a86dd18cce0ab7a1dfc9d0f94e20baf6c91317c14ce669da4111e1c --memo \u003cspan class=\"s2\"\u003e\u0026#34;just a memo field for any additional context\u0026#34;\u003c/span\u003e\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003cp\u003eAttesting to an existing payment. Treasurers have the responsibility of reviewing and validating transaction payments. To attest that a posted payment is true and accurate, use the \u003ccode\u003eattest\u003c/code\u003e command.\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003e\u003cspan class=\"c1\"\u003e# in the below example, the paymentID is 4 and the requestID is 6\u003c/span\u003e\n./daoctl --vault-file hyphanewyork.json treasury attest \u003cspan class=\"m\"\u003e4\u003c/span\u003e \u003cspan class=\"m\"\u003e6\u003c/span\u003e \u003cspan class=\"s2\"\u003e\u0026#34;3500.00 HUSD\u0026#34;\u003c/span\u003e \n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e"},{id:17,href:"https://dappdever.github.io/doks-gh-pages/docs/help/troubleshooting/",title:"Troubleshooting",description:"Solutions to common problems.",content:"\u003ch2 id=\"problems-updating-npm-packages\"\u003eProblems updating npm packages\u003c/h2\u003e\n\u003cp\u003eDelete the \u003ccode\u003e./node_modules\u003c/code\u003e folder, and run again:\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm install\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e\u003ch2 id=\"problems-with-cache\"\u003eProblems with cache\u003c/h2\u003e\n\u003cp\u003eDelete the temporary directories:\u003c/p\u003e\n\u003cdiv class=\"highlight\"\u003e\u003cpre class=\"chroma\"\u003e\u003ccode class=\"language-bash\" data-lang=\"bash\"\u003enpm run clean\n\u003c/code\u003e\u003c/pre\u003e\u003c/div\u003e"},{id:18,href:"https://dappdever.github.io/doks-gh-pages/docs/help/faq/",title:"FAQ",description:"Answers to frequently asked questions.",content:"\u003ch2 id=\"hyas\"\u003eHyas?\u003c/h2\u003e\n\u003cp\u003eDoks is a \u003ca href=\"https://gethyas.com/themes/doks/\"\u003eHyas theme\u003c/a\u003e build by the creator of Hyas.\u003c/p\u003e\n\u003ch2 id=\"footer-notice\"\u003eFooter notice?\u003c/h2\u003e\n\u003cp\u003ePlease keep it in place.\u003c/p\u003e\n\u003ch2 id=\"keyboard-shortcuts-for-search\"\u003eKeyboard shortcuts for search?\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003efocus: \u003ccode\u003e/\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eselect: \u003ccode\u003eâ†“\u003c/code\u003e and \u003ccode\u003eâ†‘\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eopen: \u003ccode\u003eEnter\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eclose: \u003ccode\u003eEsc\u003c/code\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"other-documentation\"\u003eOther documentation?\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href=\"https://docs.netlify.com/\"\u003eNetlify\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href=\"https://gohugo.io/documentation/\"\u003eHugo\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"can-i-get-support\"\u003eCan I get support?\u003c/h2\u003e\n\u003cp\u003eCreate a topic:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href=\"https://community.netlify.com/\"\u003eNetlify Community\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href=\"https://discourse.gohugo.io/\"\u003eHugo Forums\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"contact-the-creator\"\u003eContact the creator?\u003c/h2\u003e\n\u003cp\u003eSend \u003ccode\u003eh-enk\u003c/code\u003e a message:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href=\"https://community.netlify.com/\"\u003eNetlify Community\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href=\"https://discourse.gohugo.io/\"\u003eHugo Forums\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:19,href:"https://dappdever.github.io/doks-gh-pages/docs/help/",title:"Help",description:"Help Doks.",content:""},{id:20,href:"https://dappdever.github.io/doks-gh-pages/docs/about/",title:"About",description:"About",content:""},{id:21,href:"https://dappdever.github.io/doks-gh-pages/docs/prologue/",title:"Prologue",description:"Prologue Doks.",content:""},{id:22,href:"https://dappdever.github.io/doks-gh-pages/docs/",title:"Docs",description:"Docs",content:""},{id:23,href:"https://dappdever.github.io/doks-gh-pages/docs/platform/treasury/",title:"",description:null,content:""},];index.add(docs);userinput.addEventListener('input',show_results,true);suggestions.addEventListener('click',accept_suggestion,true);function show_results(){var value=this.value;var results=index.search(value,5);var entry,childs=suggestions.childNodes;var i=0,len=results.length;suggestions.classList.remove('d-none');results.forEach(function(page){entry=document.createElement('div');entry.innerHTML='<a href><span></span><span></span></a>';a=entry.querySelector('a'),t=entry.querySelector('span:first-child'),d=entry.querySelector('span:nth-child(2)');a.href=page.href;t.textContent=page.title;d.textContent=page.description;suggestions.appendChild(entry);});while(childs.length>len){suggestions.removeChild(childs[i])}}
function accept_suggestion(){while(suggestions.lastChild){suggestions.removeChild(suggestions.lastChild);}
return false;}}());